#include <iostream>
#include <string>
#include <algorithm>
#include <vector>

using namespace std;

/**
 * No214. 最短回文串（KMP）
 * 给定一个字符串 s，你可以通过在字符串前面添加字符将其转换为回文串。找到并返回可以用这种方式转换的最短回文串。

 示例 1:
输入: "aacecaaa"
输出: "aaacecaaa"

 示例 2:
输入: "abcd"
输出: "dcbabcd"

来源：力扣（LeetCode）
链接：https://leetcode-cn.com/problems/shortest-palindrome
著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
*/

/**
 * 分析：要构成回文串，则首字符必与后面的子串构成回文串，尾部则是需要逆向添加到首部左侧的补偿串
 * 时间复杂度：O(n^2)，4万长度字符串超时
 */
class Solution0 {
public:
    string shortestPalindrome(string s) {
        int len = s.length(), iterator = len - 1, leftIndex = 0, rightIndex = iterator, makeLen = 0;
        if (len < 2) {
            return s;
        }

        // 尝试找到从字符串头为起始的回文字符串
        while (iterator > 0) {
            // 判断[0, iterator]是否 是回文串
            while (leftIndex < rightIndex && s[leftIndex] == s[rightIndex]) {
                ++leftIndex;
                --rightIndex;
            }
            // 找到最大 回文字符串
            if (leftIndex >= rightIndex) {
                break;
            }
            // 初始化以判断下一个[0, iterator-1]子串是否是回文串
            leftIndex = 0;
            rightIndex = --iterator;
        }

        // 若本身已是回文，返回原字符串
        if (iterator == len - 1) {
            return s;
        }

        // 计算尾部需要补偿的剩余字符串长度（区分奇偶回文）
        makeLen = (leftIndex == rightIndex) ? len - 2 * leftIndex - 1 : len - 2 * leftIndex;

        // 构造补偿子串
        char* buff = new char[makeLen]{'\0'};
        for (int i = 0; i < makeLen; ++i) {
            buff[i] = s[len - 1 - i];
        }
        string makeStr(buff, makeLen);
        return makeStr + s;
    }
};

/**
 * 力扣官解：思路与我相同大致，只是找前面最长回文子串用了 KMP算法 O(n)
 */
class Solution {
public:
    string shortestPalindrome(string s) {
        int n = s.size();
        vector<int> fail(n, -1);
        for (int i = 1; i < n; ++i) {
            int j = fail[i - 1];
            while (j != -1 && s[j + 1] != s[i]) {
                j = fail[j];
            }
            if (s[j + 1] == s[i]) {
                fail[i] = j + 1;
            }
        }
        int best = -1;
        for (int i = n - 1; i >= 0; --i) {
            while (best != -1 && s[best + 1] != s[i]) {
                best = fail[best];
            }
            if (s[best + 1] == s[i]) {
                ++best;
            }
        }
        string add = (best == n - 1 ? "" : s.substr(best + 1, n));
        reverse(add.begin(), add.end());
        return add + s;
    }
};

int main() {
    Solution solution;
    string string1 = "";
    cout << solution.shortestPalindrome(string1) << endl;
    return 0;
}
